// src/components/auth/Register.jsx
import React, { useState } from 'react';
import { registerUser } from '../../services/api';
import { FaUser, FaLock, FaEnvelope, FaUserTag, FaSpinner } from 'react-icons/fa'; // Иконки из react-icons

const Register = () => {
  const [formData, setFormData] = useState({
    login: '',
    password: '',
    email: '',
    fullName: ''
  });
  const [errors, setErrors] = useState({});
  const [loading, setLoading] = useState(false);
  const [successMessage, setSuccessMessage] = useState('');

  // Валидация полей на фронтенде
  const validateForm = () => {
    const newErrors = {};
    if (formData.login.length < 8) {
      newErrors.login = 'Логин должен содержать минимум 8 символов';
    }
    if (formData.password.length < 8) {
      newErrors.password = 'Пароль должен содержать минимум 8 символов';
    }
    if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(formData.email)) {
      newErrors.email = 'Введите корректный email';
    }
    if (!formData.fullName.trim()) {
      newErrors.fullName = 'Полное имя не может быть пустым';
    }
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setErrors({});
    setSuccessMessage('');

    if (!validateForm()) {
      return;
    }

    setLoading(true);
    try {
      const response = await registerUser(formData);
      console.log('Регистрация успешна:', response.data);
      const { jwt } = response.data; // Предполагаем, что сервер возвращает JWT в поле jwt
      if (jwt) {
        localStorage.setItem('token', jwt);
        setSuccessMessage('Регистрация прошла успешно! Вы будете перенаправлены...');
        setTimeout(() => {
          // Перенаправление на главную страницу или страницу логина
          window.location.href = '/login'; // Замените на нужный путь
        }, 2000);
      }
    } catch (err) {
      setErrors({ server: err.response?.data?.message || 'Ошибка регистрации' });
      console.error('Ошибка:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-100">
      <div className="max-w-md w-full bg-white rounded-lg shadow-lg p-8 transform transition-all duration-300 hover:shadow-xl">
        <h2 className="text-3xl font-bold text-center text-gray-800 mb-6">Регистрация</h2>

        {/* Сообщение об успехе */}
        {successMessage && (
          <div className="mb-4 p-3 bg-green-100 text-green-700 rounded-lg text-center">
            {successMessage}
          </div>
        )}

        {/* Сообщение об ошибке */}
        {errors.server && (
          <div className="mb-4 p-3 bg-red-100 text-red-700 rounded-lg text-center">
            {errors.server}
          </div>
        )}

        <form onSubmit={handleSubmit} className="space-y-6">
          {/* Логин */}
          <div className="relative">
            <label className="block text-sm font-medium text-gray-700 mb-1">Логин</label>
            <div className="flex items-center border rounded-lg focus-within:ring-2 focus-within:ring-blue-500 transition-all">
              <FaUser className="text-gray-400 ml-3" />
              <input
                type="text"
                name="login"
                value={formData.login}
                onChange={handleChange}
                className={`w-full p-3 pl-10 border-none rounded-lg focus:outline-none focus:ring-0 ${
                  errors.login ? 'border-red-500' : ''
                }`}
                placeholder="Введите логин"
                required
              />
            </div>
            {errors.login && (
              <p className="text-red-500 text-sm mt-1">{errors.login}</p>
            )}
          </div>

          {/* Пароль */}
          <div className="relative">
            <label className="block text-sm font-medium text-gray-700 mb-1">Пароль</label>
            <div className="flex items-center border rounded-lg focus-within:ring-2 focus-within:ring-blue-500 transition-all">
              <FaLock className="text-gray-400 ml-3" />
              <input
                type="password"
                name="password"
                value={formData.password}
                onChange={handleChange}
                className={`w-full p-3 pl-10 border-none rounded-lg focus:outline-none focus:ring-0 ${
                  errors.password ? 'border-red-500' : ''
                }`}
                placeholder="Введите пароль"
                required
              />
            </div>
            {errors.password && (
              <p className="text-red-500 text-sm mt-1">{errors.password}</p>
            )}
          </div>

          {/* Email */}
          <div className="relative">
            <label className="block text-sm font-medium text-gray-700 mb-1">Email</label>
            <div className="flex items-center border rounded-lg focus-within:ring-2 focus-within:ring-blue-500 transition-all">
              <FaEnvelope className="text-gray-400 ml-3" />
              <input
                type="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                className={`w-full p-3 pl-10 border-none rounded-lg focus:outline-none focus:ring-0 ${
                  errors.email ? 'border-red-500' : ''
                }`}
                placeholder="Введите email"
                required
              />
            </div>
            {errors.email && (
              <p className="text-red-500 text-sm mt-1">{errors.email}</p>
            )}
          </div>

          {/* Полное имя */}
          <div className="relative">
            <label className="block text-sm font-medium text-gray-700 mb-1">Полное имя</label>
            <div className="flex items-center border rounded-lg focus-within:ring-2 focus-within:ring-blue-500 transition-all">
              <FaUserTag className="text-gray-400 ml-3" />
              <input
                type="text"
                name="fullName"
                value={formData.fullName}
                onChange={handleChange}
                className={`w-full p-3 pl-10 border-none rounded-lg focus:outline-none focus:ring-0 ${
                  errors.fullName ? 'border-red-500' : ''
                }`}
                placeholder="Введите полное имя"
                required
              />
            </div>
            {errors.fullName && (
              <p className="text-red-500 text-sm mt-1">{errors.fullName}</p>
            )}
          </div>

          {/* Кнопка отправки */}
          <button
            type="submit"
            disabled={loading}
            className={`w-full py-3 rounded-lg font-semibold text-white transition-all duration-300 flex items-center justify-center space-x-2 ${
              loading ? 'bg-blue-400 cursor-not-allowed' : 'bg-blue-500 hover:bg-blue-600'
            }`}
          >
            {loading ? (
              <>
                <FaSpinner className="animate-spin" />
                <span>Загрузка...</span>
              </>
            ) : (
              <span>Зарегистрироваться</span>
            )}
          </button>
        </form>

        {/* Ссылка на страницу логина */}
        <p className="text-center text-gray-600 mt-6">
          Уже есть аккаунт?{' '}
          <a href="/login" className="text-blue-500 hover:underline">
            Войдите
          </a>
        </p>
      </div>
    </div>
  );
};

export default Register;
