import { useContext } from 'react';
import { AuthContext } from '../context/authContext';

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  console.log('User in useAuth (raw):', context.user); // Полный объект
  console.log('roleNames in useAuth:', context.user?.roleNames); // Проверка roleNames
  const isAdmin = context.user?.roleNames?.includes('ROLE_ADMIN') || false;
  console.log('isAdmin:', isAdmin); // Отладка

  return {
    ...context,
    isAdmin,
  };
};
