import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../../hooks/useAuth';

const Navbar = () => {
  const { user, logout, cart } = useAuth();
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  return (
    <nav className="bg-blue-600 text-white p-4 shadow-md">
      <div className="container mx-auto flex justify-between items-center">
        <Link to="/" className="text-2xl font-bold">Книжный магазин</Link>
        <div className="flex items-center space-x-4">
          <Link to="/" className="hover:text-gray-200">Главная</Link>
          <Link to="/cart" className="hover:text-gray-200">
            Корзина ({cart.length})
          </Link>
          {user ? (
            <>
              <Link to="/orders" className="hover:text-gray-200">Мои заказы</Link>
              <button onClick={handleLogout} className="hover:text-gray-200">
                Выйти ({user.login})
              </button>
            </>
          ) : (
            <>
              <Link to="/login" className="hover:text-gray-200">Войти</Link>
              <Link to="/register" className="hover:text-gray-200">Регистрация</Link>
            </>
          )}
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
