// src/components/admin/AdminPanel.jsx
import React, { useState, useEffect } from 'react';
import { useBooks } from '../../hooks/useBooks';
import { useCategory } from '../../hooks/useCategory';
import './AdminPanel.css';

const AdminPanel = () => {
  const { books, fetchBooks, loading, error, createBook, categories, fetchCategories } = useBooks();
  const {category, setCategories} = useCategory();
  const [newBook, setNewBook] = useState({
    title: '',
    author: '',
    price: '',
    description: '',
    publisher: '',
    publicationYear: '',
    isbn: '',
    stock: '',
    categoryId: '',
  });
  const [newCategory, setNewCategory] = useState({
    name: '',
  });
  const [formErrors, setFormErrors] = useState({});
  const [categoryFormErrors, setCategoryFormErrors] = useState({});
  const [showCategoryForm, setShowCategoryForm] = useState(false); // Для переключения формы категории
  useEffect(() => {
    fetchCategories();
    fetchBooks();
  }, []);

  const validateForm = () => {
    const errors = {};
    if (!newBook.title.trim()) errors.title = 'Название не может быть пустым';
    if (!newBook.author.trim()) errors.author = 'Автор не может быть пустым';
    if (!newBook.price || parseFloat(newBook.price) <= 0) errors.price = 'Цена должна быть больше 0';
    if (!newBook.publicationYear || parseInt(newBook.publicationYear) <= 0) errors.publicationYear = 'Год издания должен быть больше 0';
    if (newBook.isbn && newBook.isbn.length !== 13) errors.isbn = 'ISBN должен содержать 13 символов';
    if (!newBook.stock || parseInt(newBook.stock) < 0) errors.stock = 'Количество на складе не может быть меньше 0';
    if (!newBook.categoryId) errors.categoryId = 'Выберите категорию';
    setFormErrors(errors);
    return Object.keys(errors).length === 0;
  };

  // Валидация формы категории
  const validateCategoryForm = () => {
    const errors = {};
    if (!newCategory.name.trim()) errors.name = 'Название категории не может быть пустым';
    setCategoryFormErrors(errors);
    return Object.keys(errors).length === 0;
  };

  const handleInputChange = (e) => {
    setNewBook({ ...newBook, [e.target.name]: e.target.value });
    setFormErrors({ ...formErrors, [e.target.name]: '' }); // Сбрасываем ошибку при изменении
  };

  // Обработчик изменений в форме категории
  const handleCategoryInputChange = (e) => {
    setNewCategory({ ...newCategory, [e.target.name]: e.target.value });
    setCategoryFormErrors({ ...categoryFormErrors, [e.target.name]: '' });
  };

  const handleAddBook = async (e) => {
    e.preventDefault();
    if (!validateForm()) return;

    try {
      await createBook({
        ...newBook,
        price: parseFloat(newBook.price),
        publicationYear: parseInt(newBook.publicationYear),
        stock: parseInt(newBook.stock),
        categoryId: parseInt(newBook.categoryId),
      });
      setNewBook({
        title: '',
        author: '',
        price: '',
        description: '',
        publisher: '',
        publicationYear: '',
        isbn: '',
        stock: '',
        categoryId: '',
      });
    } catch (err) {
      console.error('Ошибка добавления книги:', err);
    }
  };

  const handleAddCategory = async (e) => {
    e.preventDefault();
    if (!validateCategoryForm()) return;

    try {
      await createCategory({
        name: newCategory.name,
      });
      setNewCategory({ name: '' });
      setShowCategoryForm(false); // Скрываем форму после добавления
    } catch (err) {
      console.error('Ошибка добавления категории:', err);
    }
  };

  if (loading) return <p className="admin-loading">Загрузка...</p>;
  if (error) return <p className="admin-error">{error}</p>;

  return (
    <div className="admin-container">
      <h2>Панель управления книгами</h2>
      <form onSubmit={handleAddBook} className="admin-form">
        <div>
          <input
            type="text"
            name="title"
            placeholder="Название"
            value={newBook.title}
            onChange={handleInputChange}
            required
          />
          {formErrors.title && <p className="admin-error">{formErrors.title}</p>}
        </div>
        <div>
          <input
            type="text"
            name="author"
            placeholder="Автор"
            value={newBook.author}
            onChange={handleInputChange}
            required
          />
          {formErrors.author && <p className="admin-error">{formErrors.author}</p>}
        </div>
        <div>
          <input
            type="number"
            step="0.01"
            name="price"
            placeholder="Цена"
            value={newBook.price}
            onChange={handleInputChange}
            required
          />
          {formErrors.price && <p className="admin-error">{formErrors.price}</p>}
        </div>
        <div>
          <input
            type="text"
            name="description"
            placeholder="Описание"
            value={newBook.description}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <input
            type="text"
            name="publisher"
            placeholder="Издатель"
            value={newBook.publisher}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <input
            type="number"
            name="publicationYear"
            placeholder="Год издания"
            value={newBook.publicationYear}
            onChange={handleInputChange}
            required
          />
          {formErrors.publicationYear && <p className="admin-error">{formErrors.publicationYear}</p>}
        </div>
        <div>
          <input
            type="text"
            name="isbn"
            placeholder="ISBN (13 символов)"
            value={newBook.isbn}
            onChange={handleInputChange}
          />
          {formErrors.isbn && <p className="admin-error">{formErrors.isbn}</p>}
        </div>
        <div>
          <input
            type="number"
            name="stock"
            placeholder="Количество на складе"
            value={newBook.stock}
            onChange={handleInputChange}
            required
          />
          {formErrors.stock && <p className="admin-error">{formErrors.stock}</p>}
        </div>
        <div>
          <select
            name="categoryId"
            value={newBook.categoryId}
            onChange={handleInputChange}
            required
          >
            <option value="">Выберите категорию</option>
            {categories.map((category) => (
              <option key={category.id} value={category.id}>
                {category.name}
              </option>
            ))}
          </select>
          {formErrors.categoryId && <p className="admin-error">{formErrors.categoryId}</p>}
        </div>
        <button type="submit">Добавить книгу</button>
      </form>
      <div className="admin-book-list">
        <h3>Список книг</h3>
        {books.map((book) => (
          <div key={book.id} className="admin-book-item">
            <p>{book.title} - {book.author} ({book.price} ₽)</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default AdminPanel;
