import React, { useEffect, useRef, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useBooks } from '../hooks/useBooks';
import './HomePage.css';
import debounce from 'lodash/debounce';

const HomePage = () => {
  const { books, fetchBooks, loading, error, search } = useBooks();
  const navigate = useNavigate();
  const [searchQuery, setSearchQuery] = useState('');
  const searchInputRef = useRef(null);

  const debouncedSearch = debounce(async (query) => {
    if (query.trim()) {
      await search(query);
    } else {
      await fetchBooks();
    }
  }, 300);

  useEffect(() => {
    fetchBooks();
  }, []);

  useEffect(() => {
    if (searchInputRef.current) {
      searchInputRef.current.focus();
    }
  }, [books]);

  const handleSearchChange = (e) => {
    const query = e.target.value;
    setSearchQuery(query);
    debouncedSearch(query);
  };

  if (loading) return <p>Загрузка...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div className="page-container">
      <h1>Каталог книг</h1>
      <input
        ref={searchInputRef}
        type="text"
        placeholder="Поиск по названию..."
        className="search-input"
        value={searchQuery}
        onChange={handleSearchChange}
      />
      {books.length === 0 ? (
        <p>Книги не найдены</p>
      ) : (
        <div className="book-grid">
          {books.map((book) => (
            <div
              key={book.id}
              className="book-card"
              onClick={() => navigate(`/book/${book.id}`)}
            >
              {book.coverImage ? (
                <img
                  src={`http://localhost:8080${book.coverImage}`}
                  alt="Обложка"
                  style={{ width: '100px', height: 'auto', marginBottom: '10px' }}
                  onError={(e) => {
                    console.log(`Failed to load image for book ${book.id}: ${book.coverImage}`);
                    e.target.src = '/images/placeholder.jpg'; // Замените на путь к запасному изображению
                  }}
                />
              ) : (
                <p>Нет обложки</p>
              )}
              <h3>{book.title}</h3>
              <p className="book-author">{book.author}</p>
              <p className="book-price">{book.price} ₽</p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default HomePage;
