// src/components/order/OrderForm.jsx
import React, { useState, useEffect } from 'react';
import { useAuth } from '../../hooks/useAuth';
import { useAddresses } from '../../hooks/useAddresses';
import { useOrders } from '../../hooks/useOrders';
import './OrderForm.css';

const OrderForm = () => {
  const { cart, clearCart } = useAuth();
  const { addresses, fetchAddresses, createAddress } = useAddresses();
  const { placeOrder } = useOrders();
  const [address, setAddress] = useState({ city: '', street: '', house: '', apartment: '' });
  const [selectedAddress, setSelectedAddress] = useState('');
  const [showNewAddressForm, setShowNewAddressForm] = useState(false);

  useEffect(() => {
    fetchAddresses();
  }, []);

  const handleAddressChange = (e) => {
    setAddress({ ...address, [e.target.name]: e.target.value });
  };

  const handleAddAddress = async () => {
    try {
      await createAddress(address);
      setShowNewAddressForm(false);
      setAddress({ city: '', street: '', house: '', apartment: '' });
    } catch (err) {
      console.error(err);
    }
  };

  const handleSubmitOrder = async () => {
    if (!selectedAddress) {
      alert('Пожалуйста, выберите адрес доставки.');
      return;
    }

    const orderData = {
      deliveryAddressId: selectedAddress,
      orderItems: cart.map((item) => ({
        bookId: item.id,
        quantity: item.quantity,
        priceAtPurchase: item.price,
      })),
    };

    try {
      await placeOrder(orderData);
      clearCart();
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div className="order-form-container">
      <h2>Оформление заказа</h2>
      <div className="address-section">
        <h3>Выберите адрес доставки</h3>
        {addresses.length > 0 ? (
          <select
            value={selectedAddress}
            onChange={(e) => setSelectedAddress(e.target.value)}
          >
            <option value="">-- Выберите адрес --</option>
            {addresses.map((addr) => (
              <option key={addr.id} value={addr.id}>
                {addr.city}, {addr.street}, {addr.house}, {addr.apartment || ''}
              </option>
            ))}
          </select>
        ) : (
          <p>Адресов нет.</p>
        )}
        <button
          onClick={() => setShowNewAddressForm(!showNewAddressForm)}
        >
          {showNewAddressForm ? 'Отмена' : 'Добавить новый адрес'}
        </button>
      </div>

      {showNewAddressForm && (
        <div className="new-address-form">
          <h3>Новый адрес</h3>
          <input
            type="text"
            name="city"
            placeholder="Город"
            value={address.city}
            onChange={handleAddressChange}
          />
          <input
            type="text"
            name="street"
            placeholder="Улица"
            value={address.street}
            onChange={handleAddressChange}
          />
          <input
            type="text"
            name="house"
            placeholder="Дом"
            value={address.house}
            onChange={handleAddressChange}
          />
          <input
            type="text"
            name="apartment"
            placeholder="Квартира (опционально)"
            value={address.apartment}
            onChange={handleAddressChange}
          />
          <button onClick={handleAddAddress}>Сохранить адрес</button>
        </div>
      )}

      <div className="order-items">
        <h3>Товары в заказе</h3>
        {cart.map((item) => (
          <div key={item.id} className="order-item">
            <span>{item.title} (x{item.quantity})</span>
            <span>{item.price * item.quantity} ₽</span>
          </div>
        ))}
        <p>
          Итого: {cart.reduce((sum, item) => sum + item.price * item.quantity, 0)} ₽
        </p>
        <button onClick={handleSubmitOrder}>Подтвердить заказ</button>
      </div>
    </div>
  );
};

export default OrderForm;
