import { useState, useEffect } from 'react';
import { getBooks, getBookById, searchBooks, createBook as createBookApi } from '../services/api';
import { toast } from 'react-toastify';

export const useBooks = () => {
  const [books, setBooks] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleError = (err, defaultMessage) => {
    const message = err.response?.data?.message || defaultMessage;
    setError(message);
    toast.error(message);
    throw err;
  };

  const fetchBooks = async () => {
    setLoading(true);
    try {
      const response = await getBooks();
      console.log('Fetch Books Response:', response);
      if (Array.isArray(response)) {
        setBooks(response);
      } else {
        setBooks([]);
        console.warn('Unexpected response format:', response);
      }
    } catch (err) {
      handleError(err, 'Ошибка загрузки книг');
    } finally {
      setLoading(false);
    }
  };

  const fetchBookById = async (id) => {
    setLoading(true);
    try {
      const response = await getBookById(id);
      console.log('Fetch Book By Id Response:', response);
      return response;
    } catch (err) {
      handleError(err, 'Ошибка загрузки книги');
    } finally {
      setLoading(false);
    }
  };

  const search = async (title) => {
    setLoading(true);
    try {
      const response = await searchBooks(title);
      console.log('Search Books Response:', response);
      if (Array.isArray(response)) {
        setBooks(response);
      } else {
        setBooks([]);
        console.warn('Unexpected response format:', response);
      }
    } catch (err) {
      handleError(err, 'Ошибка поиска');
    } finally {
      setLoading(false);
    }
  };

  const createBook = async (bookData) => {
    setLoading(true);
    try {
      const response = await createBookApi(bookData);
      console.log('Create Book Response:', response);
      if (response && response.id) {
        setBooks((prevBooks) => [...prevBooks, response]);
        toast.success('Книга успешно добавлена!');
        return response;
      } else {
        throw new Error('Некорректный ответ от сервера');
      }
    } catch (err) {
      handleError(err, 'Ошибка добавления книги');
    } finally {
      setLoading(false);
    }
  };

  const updateBook = async (id, bookData) => {
    setLoading(true);
    try {
      const response = await updateBookApi(id, bookData);
      console.log('Update Book Response:', response);
      if (response && response.id) {
        setBooks((prevBooks) =>
          prevBooks.map((book) => (book.id === response.id ? response : book))
        );
        toast.success('Книга успешно обновлена!');
        return response;
      } else {
        throw new Error('Некорректный ответ от сервера');
      }
    } catch (err) {
      handleError(err, 'Ошибка обновления книги');
    } finally {
      setLoading(false);
    }
  };

  const deleteBook = async (id) => {
    setLoading(true);
    try {
      await deleteBookApi(id);
      console.log('Delete Book Success:', id);
      setBooks((prevBooks) => prevBooks.filter((book) => book.id !== id));
      toast.success('Книга успешно удалена!');
    } catch (err) {
      handleError(err, 'Ошибка удаления книги');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchBooks();
  }, []);

  return { books, fetchBooks, fetchBookById, search, createBook, loading, error };
};
