// src/components/auth/Register.jsx
import React, { useState } from 'react';
import { registerUser } from '../../services/api';

const Register = () => {
  const [formData, setFormData] = useState({
    login: '',
    password: '',
    email: '',
    fullName: ''
  });
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (formData.login.length < 8 || formData.password.length < 8) {
      setError('Логин и пароль должны содержать минимум 8 символов');
      return;
    }
    try {
      const response = await registerUser(formData);
      console.log('Регистрация успешна:', response.data);
      const { jwt, customer } = response.data; // Предполагаем, что сервер возвращает AuthenticationResponse
      if (jwt) {
        localStorage.setItem('token', jwt);
        // Перенаправление или другие действия после регистрации
      }
    } catch (err) {
      setError(err.response?.data?.message || 'Ошибка регистрации');
      console.error('Ошибка:', err);
    }
  };

  return (
    <div className="max-w-md mx-auto mt-10 p-6 bg-white rounded-lg shadow-md">
      <h2 className="text-2xl font-bold mb-6">Регистрация</h2>
      {error && <p className="text-red-500 mb-4">{error}</p>}
      <form onSubmit={handleSubmit}>
        <div className="mb-4">
          <label className="block text-sm font-medium mb-2">Логин</label>
          <input
            type="text"
            value={formData.login}
            onChange={(e) => setFormData({ ...formData, login: e.target.value })}
            className="w-full p-2 border rounded"
            required
          />
        </div>
        <div className="mb-4">
          <label className="block text-sm font-medium mb-2">Пароль</label>
          <input
            type="password"
            value={formData.password}
            onChange={(e) => setFormData({ ...formData, password: e.target.value })}
            className="w-full p-2 border rounded"
            required
          />
        </div>
        <div className="mb-4">
          <label className="block text-sm font-medium mb-2">Email</label>
          <input
            type="email"
            value={formData.email}
            onChange={(e) => setFormData({ ...formData, email: e.target.value })}
            className="w-full p-2 border rounded"
            required
          />
        </div>
        <div className="mb-4">
          <label className="block text-sm font-medium mb-2">Полное имя</label>
          <input
            type="text"
            value={formData.fullName}
            onChange={(e) => setFormData({ ...formData, fullName: e.target.value })}
            className="w-full p-2 border rounded"
            required
          />
        </div>
        <button type="submit" className="w-full bg-blue-500 text-white p-2 rounded hover:bg-blue-600">
          Зарегистрироваться
        </button>
      </form>
    </div>
  );
};

export default Register;
