import { useState, useEffect } from 'react';
import { getCategories, createCategory } from '../services/api';
import { toast } from 'react-toastify';

export const useCategory = () => {
  const [categories, setCategories] = useState([]);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleError = (err, defaultMessage) => {
    const message = err.response?.data?.message || defaultMessage || 'Произошла ошибка';
    setError(message);
    toast.error(message);
    setLoading(false); // Убеждаемся, что loading сбрасывается
    return Promise.reject(err);
  };

  const fetchCategories = async () => {
    setLoading(true);
    try {
      const response = await getCategories();
      setCategories(response.data);
    } catch (err) {
      handleError(err, 'Ошибка загрузки категорий');
    } finally {
      setLoading(false);
    }
  };

  const createCategory = async (categoryData) => {
    setLoading(true);
    try {
      const response = await createCategory(categoryData);
      setCategories((prevCategories) => [...prevCategories, response.data]); // Безопасное обновление
      toast.success('Категория успешно добавлена!');
      return response.data;
    } catch (err) {
      handleError(err, 'Ошибка добавления категории');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchCategories();
  }, []);

  return { categories, fetchCategories, createCategory, loading, error };
};
