import React, { useState, useEffect } from 'react';
import { useAuth } from '../hooks/useAuth';
import { topUpBalanceWithCard, updateProfile } from '../services/api';
import { toast } from 'react-toastify';
import './ProfilePage.css';

const ProfilePage = () => {
  const { user, setUser } = useAuth();
  const [amount, setAmount] = useState('');
  const [isEditing, setIsEditing] = useState(false);
  const [editData, setEditData] = useState({ login: '', email: '' });
  const [cardData, setCardData] = useState({ cardNumber: '', expiryDate: '', cvv: '' });
  const [isProcessing, setIsProcessing] = useState(false);

  useEffect(() => {
    if (user) {
      setEditData({ login: user.login || '', email: user.email || '' });
    }
  }, [user]);

  const validateCardDetails = () => {
    const cleanCardNumber = cardData.cardNumber.replace(/\s/g, '');
    if (cleanCardNumber.length !== 16 || !/^\d+$/.test(cleanCardNumber)) {
      toast.error('Номер карты должен содержать 16 цифр');
      return false;
    }
    if (!/^(0[1-9]|1[0-2])\/\d{2}$/.test(cardData.expiryDate)) {
      toast.error('Срок действия должен быть в формате MM/YY');
      return false;
    }
    const [month, year] = cardData.expiryDate.split('/');
    const currentYear = new Date().getFullYear() % 100;
    const currentMonth = new Date().getMonth() + 1;
    if (
      parseInt(month) < 1 ||
      parseInt(month) > 12 ||
      parseInt(year) < currentYear ||
      (parseInt(year) === currentYear && parseInt(month) < currentMonth)
    ) {
      toast.error('Срок действия карты истёк или указан неверно');
      return false;
    }
    if (cardData.cvv.length !== 3 || !/^\d+$/.test(cardData.cvv)) {
      toast.error('CVV должен содержать 3 цифры');
      return false;
    }
    return true;
  };

  const handleTopUp = async () => {
    if (!amount || parseFloat(amount) <= 0) {
      toast.error('Сумма должна быть больше 0');
      return;
    }
    if (!cardData.cardNumber || !cardData.expiryDate || !cardData.cvv) {
      toast.error('Пожалуйста, заполните все поля карты');
      return;
    }

    if (!validateCardDetails()) {
      return;
    }

    setIsProcessing(true);
    try {
      await new Promise((resolve) => setTimeout(resolve, 2000));
      const cleanCardNumber = cardData.cardNumber.replace(/\s/g, '');
      const response = await topUpBalanceWithCard({
        amount: parseFloat(amount),
        cardNumber: cleanCardNumber,
        expiryDate: cardData.expiryDate,
        cvv: cardData.cvv
      });
      setUser(response);
      toast.success('Баланс успешно пополнен!');
      setAmount('');
      setCardData({ cardNumber: '', expiryDate: '', cvv: '' });
    } catch (err) {
      toast.error('Ошибка пополнения баланса: ' + (err.response?.data?.message || 'Проверьте данные карты'));
      console.error(err);
    } finally {
      setIsProcessing(false);
    }
  };

  const handleEditToggle = () => {
    setIsEditing(!isEditing);
  };

  const handleEditChange = (e) => {
    setEditData({ ...editData, [e.target.name]: e.target.value });
  };

  const handleCardChange = (e) => {
    const { name, value } = e.target;
    if (name === 'cardNumber') {
      const digitsOnly = value.replace(/\D/g, '');
      const formattedValue = digitsOnly
        .slice(0, 16)
        .replace(/(\d{4})(?=\d)/g, '$1 ');
      setCardData({ ...cardData, cardNumber: formattedValue });
    } else if (name === 'expiryDate') {
      const digitsOnly = value.replace(/\D/g, '');
      let formattedValue = digitsOnly.slice(0, 4);
      if (digitsOnly.length >= 2) {
        formattedValue = `${digitsOnly.slice(0, 2)}/${digitsOnly.slice(2, 4)}`;
      }
      setCardData({ ...cardData, expiryDate: formattedValue });
    } else if (name === 'cvv') {
      const digitsOnly = value.replace(/\D/g, '').slice(0, 3);
      setCardData({ ...cardData, cvv: digitsOnly });
    }
  };

  const handleSaveProfile = async () => {
    console.log('Sending profile data:', editData);
    try {
      const response = await updateProfile(editData);
      setUser(response); // Update the user context with the new data
      setIsEditing(false);
      toast.success('Профиль обновлён!');
    } catch (err) {
      toast.error('Ошибка обновления профиля: ' + (err.response?.data?.message || 'Проверьте данные'));
      console.error(err);
    }
  };

  if (!user) return <p>Пожалуйста, войдите в аккаунт</p>;

  return (
    <div className="profile-container">
      <h2>Профиль</h2>
      {isEditing ? (
        <div className="edit-profile-section">
          <h3>Редактирование профиля</h3>
          <input
            type="text"
            name="login"
            value={editData.login}
            onChange={handleEditChange}
            placeholder="Логин"
          />
          <input
            type="email"
            name="email"
            value={editData.email}
            onChange={handleEditChange}
            placeholder="Email"
          />
          <button onClick={handleSaveProfile}>Сохранить</button>
          <button onClick={handleEditToggle}>Отмена</button>
        </div>
      ) : (
        <>
          <p>Логин: {user.login}</p>
          <p>Email: {user.email}</p>
          <p>Баланс: {user.balance || 0} ₽</p>
          <button onClick={handleEditToggle}>Редактировать профиль</button>
        </>
      )}
      <div className="topup-section">
        <h3>Пополнить баланс</h3>
        <input
          type="number"
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
          placeholder="Сумма"
          disabled={isProcessing}
        />
        <input
          type="text"
          name="cardNumber"
          value={cardData.cardNumber}
          onChange={handleCardChange}
          placeholder="Номер карты (16 цифр)"
          maxLength="19"
          disabled={isProcessing}
        />
        <input
          type="text"
          name="expiryDate"
          value={cardData.expiryDate}
          onChange={handleCardChange}
          placeholder="Срок действия (MM/YY)"
          maxLength="5"
          disabled={isProcessing}
        />
        <input
          type="text"
          name="cvv"
          value={cardData.cvv}
          onChange={handleCardChange}
          placeholder="CVV (3 цифры)"
          maxLength="3"
          disabled={isProcessing}
        />
        <button onClick={handleTopUp} disabled={isProcessing}>
          {isProcessing ? 'Обработка...' : 'Пополнить'}
        </button>
      </div>
      {user.transactions && user.transactions.length > 0 && (
        <div className="transaction-history">
          <h3>История транзакций</h3>
          <ul>
            {user.transactions.map((tx, index) => (
              <li key={index}>
                {new Date(tx.date).toLocaleString()} - {tx.amount} ₽ -{' '}
                {tx.status} (Карта: **** **** **** {tx.cardLastFour})
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default ProfilePage;
