import React, { useState, useEffect } from 'react';
import { useBooks } from '../../hooks/useBooks';

const AdminDiscountForm = ({ bookId }) => {
    const { createDiscount, updateDiscount, deleteDiscount, getDiscountsForBook, loading, error } = useBooks();
    const [discounts, setDiscounts] = useState([]);
    const [discount, setDiscount] = useState({
        discountPercentage: '',
        startDate: '',
        endDate: '',
    });
    const [editingDiscountId, setEditingDiscountId] = useState(null);

    useEffect(() => {
        const loadDiscounts = async () => {
            const data = await getDiscountsForBook(bookId);
            setDiscounts(data || []);
        };
        loadDiscounts();
    }, [bookId]);

    const handleChange = (e) => {
        setDiscount({ ...discount, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const discountData = {
            discountPercentage: parseFloat(discount.discountPercentage),
            startDate: new Date(discount.startDate).toISOString(),
            endDate: new Date(discount.endDate).toISOString(),
            active: true,
        };
        try {
            if (editingDiscountId) {
                await updateDiscount(editingDiscountId, discountData);
            } else {
                await createDiscount(bookId, discountData);
            }
            setDiscount({ discountPercentage: '', startDate: '', endDate: '' });
            setEditingDiscountId(null);
            const updatedDiscounts = await getDiscountsForBook(bookId);
            setDiscounts(updatedDiscounts || []);
        } catch (err) {
            console.error('Ошибка при сохранении скидки:', err);
        }
    };

    const handleEdit = (discount) => {
        setDiscount({
            discountPercentage: discount.discountPercentage,
            startDate: new Date(discount.startDate).toISOString().slice(0, 16),
            endDate: new Date(discount.endDate).toISOString().slice(0, 16),
        });
        setEditingDiscountId(discount.id);
    };

    const handleDelete = async (id) => {
        try {
            await deleteDiscount(id);
            const updatedDiscounts = await getDiscountsForBook(bookId);
            setDiscounts(updatedDiscounts || []);
        } catch (err) {
            console.error('Ошибка при удалении скидки:', err);
        }
    };

    if (loading) return <p>Загрузка...</p>;
    if (error) return <p>{error}</p>;

    return (
        <div>
            <h3>Управление скидками</h3>
            <form onSubmit={handleSubmit}>
                <input
                    type="number"
                    name="discountPercentage"
                    placeholder="Процент скидки (0-100)"
                    value={discount.discountPercentage}
                    onChange={handleChange}
                    step="0.1"
                    min="0"
                    max="100"
                    required
                />
                <input
                    type="datetime-local"
                    name="startDate"
                    value={discount.startDate}
                    onChange={handleChange}
                    required
                />
                <input
                    type="datetime-local"
                    name="endDate"
                    value={discount.endDate}
                    onChange={handleChange}
                    required
                />
                <button type="submit">{editingDiscountId ? 'Обновить' : 'Добавить'}</button>
            </form>
            <ul>
                {discounts.map((d) => (
                    <li key={d.id}>
                        {d.discountPercentage}% с {new Date(d.startDate).toLocaleString()} по {new Date(d.endDate).toLocaleString()}
                        <button onClick={() => handleEdit(d)}>Редактировать</button>
                        <button onClick={() => handleDelete(d.id)}>Удалить</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default AdminDiscountForm;
