// src/components/admin/AdminPanel.jsx
import React, { useState } from 'react';
import { useBooks } from '../../hooks/useBooks';
import './AdminPanel.css';

const AdminPanel = () => {
  const { books, fetchBooks, loading, error, createBook } = useBooks();
  const [newBook, setNewBook] = useState({
    title: '',
    author: '',
    price: '',
    description: '',
    publisher: '',
    publicationYear: '',
    isbn: '',
    stock: '',
  });

  const handleInputChange = (e) => {
    setNewBook({ ...newBook, [e.target.name]: e.target.value });
  };

  const handleAddBook = async (e) => {
    e.preventDefault();
    try {
      await createBook(newBook);
      setNewBook({
        title: '',
        author: '',
        price: '',
        description: '',
        publisher: '',
        publicationYear: '',
        isbn: '',
        stock: '',
      });
      fetchBooks();
    } catch (err) {
      console.error('Ошибка добавления книги:', err);
    }
  };

  if (loading) return <p className="admin-loading">Загрузка...</p>;
  if (error) return <p className="admin-error">{error}</p>;

  return (
    <div className="admin-container">
      <h2>Панель управления книгами</h2>
      <form onSubmit={handleAddBook} className="admin-form">
        <input
          type="text"
          name="title"
          placeholder="Название"
          value={newBook.title}
          onChange={handleInputChange}
          required
        />
        <input
          type="text"
          name="author"
          placeholder="Автор"
          value={newBook.author}
          onChange={handleInputChange}
          required
        />
        <input
          type="number"
          name="price"
          placeholder="Цена"
          value={newBook.price}
          onChange={handleInputChange}
          required
        />
        <input
          type="text"
          name="description"
          placeholder="Описание"
          value={newBook.description}
          onChange={handleInputChange}
          required
        />
        <input
          type="text"
          name="publisher"
          placeholder="Издатель"
          value={newBook.publisher}
          onChange={handleInputChange}
          required
        />
        <input
          type="number"
          name="publicationYear"
          placeholder="Год издания"
          value={newBook.publicationYear}
          onChange={handleInputChange}
          required
        />
        <input
          type="text"
          name="isbn"
          placeholder="ISBN"
          value={newBook.isbn}
          onChange={handleInputChange}
          required
        />
        <input
          type="number"
          name="stock"
          placeholder="Количество на складе"
          value={newBook.stock}
          onChange={handleInputChange}
          required
        />
        <button type="submit">Добавить книгу</button>
      </form>
      <div className="admin-book-list">
        <h3>Список книг</h3>
        {books.map((book) => (
          <div key={book.id} className="admin-book-item">
            <p>{book.title} - {book.author} ({book.price} ₽)</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default AdminPanel;
