import { useState, useEffect } from 'react';
import { createOrder, getOrders } from '../services/api';
import { toast } from 'react-toastify';

export const useOrders = () => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const fetchOrders = async () => {
    setLoading(true);
    try {
      const response = await getOrders();
      setOrders(response.data);
    } catch (err) {
      setError(err.response?.data?.message || 'Ошибка загрузки заказов');
      toast.error(err.response?.data?.message || 'Ошибка загрузки заказов');
    } finally {
      setLoading(false);
    }
  };

  const placeOrder = async (orderData) => {
    setLoading(true);
    try {
      const response = await createOrder(orderData);
      toast.success('Заказ успешно создан!');
      return response.data;
    } catch (err) {
      setError(err.response?.data?.message || 'Ошибка создания заказа');
      toast.error(err.response?.data?.message || 'Ошибка создания заказа');
      throw err;
    } finally {
      setLoading(false);
    }
  };

  return { orders, fetchOrders, placeOrder, loading, error };
};
