import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useBooks } from '../hooks/useBooks';
import './HomePage.css';

const HomePage = () => {
  const { books, fetchBooks, loading, error, search } = useBooks(); // Добавляем search
  const navigate = useNavigate();
  const [searchQuery, setSearchQuery] = useState(''); // Состояние для поискового запроса

  useEffect(() => {
    fetchBooks();
  }, []);

  // Обработчик изменения текста в поле поиска
  const handleSearchChange = async (e) => {
    const query = e.target.value;
    setSearchQuery(query);
    if (query.trim()) {
      await search(query); // Вызываем поиск, если запрос не пустой
    } else {
      await fetchBooks(); // Загружаем все книги, если запрос пустой
    }
  };

  if (loading) return <p>Загрузка...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div className="page-container">
      <h1>Каталог книг</h1>
      <input
        type="text"
        placeholder="Поиск по названию..."
        className="search-input"
        value={searchQuery}
        onChange={handleSearchChange} // Обработчик изменения
      />
      {books.length === 0 ? (
        <p>Книги не найдены</p>
      ) : (
        <div className="book-grid">
          {books.map((book) => (
            <div
              key={book.id}
              className="book-card"
              onClick={() => navigate(`/book/${book.id}`)}
            >
              <h3>{book.title}</h3>
              <p className="book-author">{book.author}</p>
              <p className="book-price">{book.price} ₽</p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default HomePage;
