import React, { useEffect } from 'react';
import { useAuth } from '../../hooks/useAuth';
import { useOrders } from '../../hooks/useOrders';
import './OrderHistory.css';

const OrderHistory = () => {
  const { orders, fetchOrders, loading, error } = useOrders();
  const { user, token } = useAuth();

  useEffect(() => {
    if (user && token) {
      fetchOrders();
    } else {
      // Очистка заказов, если пользователь не авторизован
      setOrders([]); // Убедитесь, что setOrders доступен, если вы его добавили в useOrders
    }
  }, [user, token]); // Убрали fetchOrders и setOrders из зависимостей

  if (loading) return <p className="order-history-loading">Загрузка...</p>;
  if (error) return <p className="order-history-error">{error}</p>;

  return (
    <div className="order-history-container">
      <h2>История заказов</h2>
      {orders.length === 0 ? (
        <p className="order-history-empty">У вас пока нет заказов.</p>
      ) : (
        <div>
          {orders.map((order) => (
            <div key={order.id} className="order-history-item">
              <p><strong>Заказ №{order.id}</strong></p>
              <p>Дата: {new Date(order.orderDate).toLocaleDateString()}</p>
              <p>Статус: {order.status}</p>
              <p>Итого: {order.total} ₽</p>
              <p>Товары:</p>
              <ul>
                {order.orderItems.map((item) => (
                  <li key={item.bookId}>
                    Книга ID: {item.bookId}, Кол-во: {item.quantity}, Цена: {item.priceAtPurchase} ₽
                  </li>
                ))}
              </ul>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default OrderHistory;
