import { useState, useEffect } from 'react';
import { getBooks, getBookById, searchBooks, getCategories, createBook, distributeBook, deductStock } from '../services/api';
import { toast } from 'react-toastify';

export const useBooks = () => {
  const [books, setBooks] = useState([]);
  const [categories, setCategories] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleError = (err, defaultMessage) => {
    const message = err.response?.data?.message || defaultMessage;
    setError(message);
    toast.error(message);
    return message;
  };

  const fetchBooks = async () => {
    setLoading(true);
    try {
      const response = await getBooks();
      setBooks(response.data);
      setError(null);
    } catch (err) {
      handleError(err, 'Ошибка загрузки книг');
    } finally {
      setLoading(false);
    }
  };

  const fetchBookById = async (id) => {
    setLoading(true);
    try {
      const response = await getBookById(id);
      setError(null);
      return response.data;
    } catch (err) {
      handleError(err, 'Ошибка загрузки книги');
    } finally {
      setLoading(false);
    }
  };

  const fetchCategories = async () => {
    setLoading(true);
    try {
      const response = await getCategories();
      setCategories(response.data);
      setError(null);
    } catch (err) {
      handleError(err, 'Ошибка загрузки категорий');
    } finally {
      setLoading(false);
    }
  };

  const search = async (title) => {
    setLoading(true);
    try {
      const response = await searchBooks(title);
      setBooks(response.data);
      setError(null);
    } catch (err) {
      handleError(err, 'Ошибка поиска');
    } finally {
      setLoading(false);
    }
  };

  const createBook = async (bookData) => {
    setLoading(true);
    try {
      const response = await createBook(bookData);
      toast.success('Книга успешно добавлена!');
      await fetchBooks(); // Обновляем список после добавления
      setError(null);
      return response.data;
    } catch (err) {
      const message = handleError(err, 'Ошибка добавления книги');
      throw new Error(message);
    } finally {
      setLoading(false);
    }
  };

  const distributeBookToWarehouse = async (bookId, warehouseId, stock) => {
    setLoading(true);
    try {
      await distributeBook(bookId, warehouseId, stock);
      toast.success('Книга успешно распределена!');
      await fetchBooks(); // Обновляем список
      setError(null);
    } catch (err) {
      handleError(err, 'Ошибка распределения книги');
    } finally {
      setLoading(false);
    }
  };

  const deductBookStock = async (bookId, quantity) => {
    setLoading(true);
    try {
      await deductStock(bookId, quantity);
      toast.success('Запас успешно вычтен!');
      await fetchBooks(); // Обновляем список
      setError(null);
    } catch (err) {
      handleError(err, 'Ошибка вычитания запаса');
    } finally {
      setLoading(false);
    }
  };

  // Вызываем только один раз при монтировании
  useEffect(() => {
    fetchCategories();
    fetchBooks();
  }, []);

  return { books, categories, fetchBooks, fetchBookById, fetchCategories, search, createBook, distributeBookToWarehouse, deductBookStock, loading, error };
};
