import React from 'react';
import { useAuth } from '../../hooks/useAuth';

const BookDetails = ({ book }) => {
  const { addToCart, user } = useAuth();

  return (
    <div className="bg-white p-6 rounded-lg shadow-lg">
      <h2 className="text-3xl font-semibold text-gray-800 mb-4">{book.title}</h2>
      <p className="text-gray-600 mb-2"><strong>Автор:</strong> {book.author}</p>
      <p className="text-gray-600 mb-2"><strong>Цена:</strong> {book.price} ₽</p>
      <p className="text-gray-600 mb-2"><strong>Описание:</strong> {book.description}</p>
      <p className="text-gray-600 mb-2"><strong>Издатель:</strong> {book.publisher}</p>
      <p className="text-gray-600 mb-2"><strong>Год издания:</strong> {book.publicationYear}</p>
      <p className="text-gray-600 mb-2"><strong>ISBN:</strong> {book.isbn}</p>
      <p className="text-gray-600 mb-4"><strong>В наличии:</strong> {book.stock} шт.</p>
      {user && (
        <button
          onClick={() => addToCart(book)}
          className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600 transition"
        >
          В корзину
        </button>
      )}
    </div>
  );
};

export default BookDetails;
