import { useState, useEffect } from 'react';
import { getBooks, getBookById, searchBooks, getCategories, createBook as createBookApi } from '../services/api';
import { toast } from 'react-toastify';

export const useBooks = () => {
  const [books, setBooks] = useState([]);
  const [categories, setCategories] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Утилита для обработки ошибок, теперь внутри хука, чтобы иметь доступ к setError
  const handleError = (err, defaultMessage) => {
    const message = err.response?.data?.message || defaultMessage;
    setError(message);
    toast.error(message);
    throw err;
  };

  useEffect(() => {
    fetchBooks();
    fetchCategories();
  }, []);

  const fetchBooks = async () => {
    setLoading(true);
    try {
      const response = await getBooks();
      setBooks(response.data);
    } catch (err) {
      handleError(err, 'Ошибка загрузки книг');
    } finally {
      setLoading(false);
    }
  };

  const fetchBookById = async (id) => {
    setLoading(true);
    try {
      const response = await getBookById(id);
      const book = response.data;
      return book;
    } catch (err) {
      handleError(err, 'Ошибка загрузки книги');
    } finally {
      setLoading(false);
    }
  };

  const fetchCategories = async () => {
    setLoading(true);
    try {
      const response = await getCategories();
      setCategories(response.data);
    } catch (err) {
      handleError(err, 'Ошибка загрузки категорий');
    } finally {
      setLoading(false);
    }
  };

  const search = async (title) => {
    setLoading(true);
    try {
      const response = await searchBooks(title);
      setBooks(response.data);
    } catch (err) {
      handleError(err, 'Ошибка поиска');
    } finally {
      setLoading(false);
    }
  };

  const createBook = async (bookData) => {
    setLoading(true);
    try {
      const response = await createBookApi(bookData);
      setBooks([...books, response.data]);
      toast.success('Книга успешно добавлена!');
      return response.data;
    } catch (err) {
      handleError(err, 'Ошибка добавления книги');
    } finally {
      setLoading(false);
    }
  };

  return { books, categories, fetchBooks, fetchBookById, fetchCategories, search, createBook, loading, error };
};
