import { useState, useEffect } from 'react';
import { getOrders, createOrder } from '../services/api';

export const useOrders = () => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const fetchOrders = async () => {
    setLoading(true);
    try {
      const response = await getOrders();
      setOrders(response.data);
    } catch (err) {
      setError(err.response?.data?.message || 'Ошибка загрузки заказов');
      console.error('Fetch orders error:', err.response?.data);
    } finally {
      setLoading(false);
    }
  };

  const placeOrder = async (orderData) => {
    setLoading(true);
    try {
      const response = await createOrder(orderData);
      setOrders([...orders, response.data]);
      return response.data;
    } catch (err) {
      setError(err.response?.data?.message || 'Ошибка создания заказа');
      throw err;
    } finally {
      setLoading(false);
    }
  };

  // Удалите useEffect из хука, чтобы вызывать fetchOrders вручную
  return { orders, fetchOrders, placeOrder, loading, error };
};
