import React, { useState } from 'react';
import { useAuth } from '../../hooks/useAuth';
import { useAddresses } from '../../hooks/useAddresses';
import { useOrders } from '../../hooks/useOrders';

const OrderForm = () => {
  const { cart, clearCart } = useAuth();
  const { addresses, fetchAddresses, createAddress } = useAddresses();
  const { placeOrder } = useOrders();
  const [address, setAddress] = useState({ city: '', street: '', house: '', apartment: '' });
  const [selectedAddress, setSelectedAddress] = useState('');
  const [showNewAddressForm, setShowNewAddressForm] = useState(false);

  React.useEffect(() => {
    fetchAddresses();
  }, []);

  const handleAddressChange = (e) => {
    setAddress({ ...address, [e.target.name]: e.target.value });
  };

  const handleAddAddress = async () => {
    try {
      await createAddress(address);
      setShowNewAddressForm(false);
      setAddress({ city: '', street: '', house: '', apartment: '' });
    } catch (err) {
      console.error(err);
    }
  };

  const handleSubmitOrder = async () => {
    if (!selectedAddress) {
      alert('Пожалуйста, выберите адрес доставки.');
      return;
    }

    const orderData = {
      deliveryAddressId: selectedAddress,
      orderItems: cart.map((item) => ({
        bookId: item.id,
        quantity: item.quantity,
        priceAtPurchase: item.price,
      })),
    };

    try {
      await placeOrder(orderData);
      clearCart();
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div className="container mx-auto p-4">
      <h2 className="text-2xl font-semibold mb-4">Оформление заказа</h2>
      <div className="mb-6">
        <h3 className="text-lg font-semibold mb-2">Выберите адрес доставки</h3>
        {addresses.length > 0 ? (
          <select
            value={selectedAddress}
            onChange={(e) => setSelectedAddress(e.target.value)}
            className="w-full p-3 border rounded-lg"
          >
            <option value="">-- Выберите адрес --</option>
            {addresses.map((addr) => (
              <option key={addr.id} value={addr.id}>
                {addr.city}, {addr.street}, {addr.house}, {addr.apartment || ''}
              </option>
            ))}
          </select>
        ) : (
          <p className="text-gray-600">Адресов нет.</p>
        )}
        <button
          onClick={() => setShowNewAddressForm(!showNewAddressForm)}
          className="mt-2 text-blue-500 hover:underline"
        >
          {showNewAddressForm ? 'Отмена' : 'Добавить новый адрес'}
        </button>
      </div>

      {showNewAddressForm && (
        <div className="mb-6 bg-white p-4 rounded-lg shadow">
          <h3 className="text-lg font-semibold mb-2">Новый адрес</h3>
          <input
            type="text"
            name="city"
            placeholder="Город"
            value={address.city}
            onChange={handleAddressChange}
            className="w-full p-3 mb-2 border rounded-lg"
          />
          <input
            type="text"
            name="street"
            placeholder="Улица"
            value={address.street}
            onChange={handleAddressChange}
            className="w-full p-3 mb-2 border rounded-lg"
          />
          <input
            type="text"
            name="house"
            placeholder="Дом"
            value={address.house}
            onChange={handleAddressChange}
            className="w-full p-3 mb-2 border rounded-lg"
          />
          <input
            type="text"
            name="apartment"
            placeholder="Квартира (опционально)"
            value={address.apartment}
            onChange={handleAddressChange}
            className="w-full p-3 mb-2 border rounded-lg"
          />
          <button
            onClick={handleAddAddress}
            className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
          >
            Сохранить адрес
          </button>
        </div>
      )}

      <div className="bg-white p-4 rounded-lg shadow">
        <h3 className="text-lg font-semibold mb-2">Товары в заказе</h3>
        {cart.map((item) => (
          <div key={item.id} className="flex justify-between mb-2">
            <span>{item.title} (x{item.quantity})</span>
            <span>{item.price * item.quantity} ₽</span>
          </div>
        ))}
        <p className="text-xl font-semibold mt-4">
          Итого: {cart.reduce((sum, item) => sum + item.price * item.quantity, 0)} ₽
        </p>
        <button
          onClick={handleSubmitOrder}
          className="mt-4 bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600"
        >
          Подтвердить заказ
        </button>
      </div>
    </div>
  );
};

export default OrderForm;
