import { useState, useEffect } from 'react';
import { getBooks, getBookById, searchBooks, getCategories, createBook as createBookApi } from '../services/api';
import { toast } from 'react-toastify';

export const useBooks = () => {
  const [books, setBooks] = useState([]);
  const [categories, setCategories] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchBooks();
    fetchCategories();
  }, []);

  const fetchBooks = async () => {
    setLoading(true);
    try {
      const response = await getBooks();
      setBooks(response.data);
    } catch (err) {
      setError(err.response?.data?.message || 'Ошибка загрузки книг');
      toast.error(err.response?.data?.message || 'Ошибка загрузки книг');
    } finally {
      setLoading(false);
    }
  };

  const fetchBookById = async (id) => {
    setLoading(true);
    try {
      const response = await getBookById(id);
      return response.data;
    } catch (err) {
      setError(err.response?.data?.message || 'Ошибка загрузки книги');
      toast.error(err.response?.data?.message || 'Ошибка загрузки книги');
      throw err;
    } finally {
      setLoading(false);
    }
  };

  const fetchCategories = async () => {
    setLoading(true);
    try {
      const response = await getCategories();
      setCategories(response.data);
    } catch (err) {
      setError(err.response?.data?.message || 'Ошибка загрузки категорий');
      toast.error(err.response?.data?.message || 'Ошибка загрузки категорий');
    } finally {
      setLoading(false);
    }
  };

  const search = async (title) => {
    setLoading(true);
    try {
      const response = await searchBooks(title);
      setBooks(response.data);
    } catch (err) {
      setError(err.response?.data?.message || 'Ошибка поиска');
      toast.error(err.response?.data?.message || 'Ошибка поиска');
    } finally {
      setLoading(false);
    }
  };

  const createBook = async (bookData) => {
    setLoading(true);
    try {
      const response = await createBookApi(bookData);
      setBooks([...books, response.data]);
      toast.success('Книга успешно добавлена!');
      return response.data;
    } catch (err) {
      setError(err.response?.data?.message || 'Ошибка добавления книги');
      toast.error(err.response?.data?.message || 'Ошибка добавления книги');
      throw err;
    } finally {
      setLoading(false);
    }
  };

  return { books, categories, fetchBooks, fetchBookById, fetchCategories, search, createBook, loading, error };
};
