import React, { useState, useEffect } from 'react';
import { useBooks } from '../../hooks/useBooks';
import { useWarehouses } from '../../hooks/useWarehouses'; // Импортируем хук для складов
import './AdminPanel.css';

const AdminPanel = () => {
  const { books, fetchBooks, loading, error, createBook, categories, fetchCategories, distributeBookToWarehouse, deductBookStock } = useBooks();
  const { warehouses, fetchWarehouses, loading: warehousesLoading, error: warehousesError } = useWarehouses();
  const [newBook, setNewBook] = useState({
    title: '',
    author: '',
    price: '',
    description: '',
    publisher: '',
    publicationYear: '',
    isbn: '',
    stock: '',
    categoryId: '',
  });
  const [newWarehouse, setNewWarehouse] = useState({ name: '', location: '' });
  const [distribution, setDistribution] = useState({ bookId: '', warehouseId: '', stock: '' });
  const [formErrors, setFormErrors] = useState({});
  const [currentPage, setCurrentPage] = useState(1);
  const [view, setView] = useState('books'); // 'books' или 'warehouses'
  const booksPerPage = 5;

  useEffect(() => {
    fetchCategories();
    fetchBooks();
    fetchWarehouses();
  }, []);

  const validateBookForm = () => {
    const errors = {};
    if (!newBook.title.trim()) errors.title = 'Название не может быть пустым';
    if (!newBook.author.trim()) errors.author = 'Автор не может быть пустым';
    if (!newBook.price || parseFloat(newBook.price) <= 0) errors.price = 'Цена должна быть больше 0';
    if (!newBook.publicationYear || parseInt(newBook.publicationYear) <= 0) errors.publicationYear = 'Год издания должен быть больше 0';
    if (newBook.isbn && newBook.isbn.length !== 13) errors.isbn = 'ISBN должен содержать 13 символов';
    if (!newBook.stock || parseInt(newBook.stock) < 0) errors.stock = 'Количество на складе не может быть меньше 0';
    if (!newBook.categoryId) errors.categoryId = 'Выберите категорию';
    setFormErrors(errors);
    return Object.keys(errors).length === 0;
  };

  const validateWarehouseForm = () => {
    const errors = {};
    if (!newWarehouse.name.trim()) errors.name = 'Название склада не может быть пустым';
    setFormErrors(errors);
    return Object.keys(errors).length === 0;
  };

  const validateDistributionForm = () => {
    const errors = {};
    if (!distribution.bookId) errors.bookId = 'Выберите книгу';
    if (!distribution.warehouseId) errors.warehouseId = 'Выберите склад';
    if (!distribution.stock || parseInt(distribution.stock) <= 0) errors.stock = 'Количество должно быть больше 0';
    setFormErrors(errors);
    return Object.keys(errors).length === 0;
  };

  const handleBookInputChange = (e) => {
    setNewBook({ ...newBook, [e.target.name]: e.target.value });
    setFormErrors({ ...formErrors, [e.target.name]: '' });
  };

  const handleWarehouseInputChange = (e) => {
    setNewWarehouse({ ...newWarehouse, [e.target.name]: e.target.value });
    setFormErrors({ ...formErrors, [e.target.name]: '' });
  };

  const handleDistributionChange = (e) => {
    setDistribution({ ...distribution, [e.target.name]: e.target.value });
    setFormErrors({ ...formErrors, [e.target.name]: '' });
  };

  const handleAddBook = async (e) => {
    e.preventDefault();
    if (!validateBookForm()) return;

    try {
      await createBook({
        ...newBook,
        price: parseFloat(newBook.price),
        publicationYear: parseInt(newBook.publicationYear),
        stock: parseInt(newBook.stock),
        categoryId: parseInt(newBook.categoryId),
      });
      setNewBook({
        title: '',
        author: '',
        price: '',
        description: '',
        publisher: '',
        publicationYear: '',
        isbn: '',
        stock: '',
        categoryId: '',
      });
      fetchBooks();
    } catch (err) {
      console.error('Ошибка добавления книги:', err);
    }
  };

  const handleAddWarehouse = async (e) => {
    e.preventDefault();
    if (!validateWarehouseForm()) return;

    try {
      await fetch('/api/warehouses', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(newWarehouse),
      });
      setNewWarehouse({ name: '', location: '' });
      fetchWarehouses(); // Обновляем список складов
    } catch (err) {
      console.error('Ошибка добавления склада:', err);
    }
  };

  const handleDistributeBook = async (e) => {
    e.preventDefault();
    if (!validateDistributionForm()) return;

    try {
      await distributeBookToWarehouse(parseInt(distribution.bookId), parseInt(distribution.warehouseId), parseInt(distribution.stock));
      setDistribution({ bookId: '', warehouseId: '', stock: '' });
      fetchBooks();
    } catch (err) {
      console.error('Ошибка распределения книги:', err);
    }
  };

  if (loading || warehousesLoading) return <p className="admin-loading">Загрузка...</p>;
  if (error || warehousesError) return <p className="admin-error">{error || warehousesError}</p>;

  const indexOfLastBook = currentPage * booksPerPage;
  const indexOfFirstBook = indexOfLastBook - booksPerPage;
  const currentBooks = books.slice(indexOfFirstBook, indexOfLastBook);
  const totalPages = Math.ceil(books.length / booksPerPage);

  const paginate = (pageNumber) => setCurrentPage(pageNumber);

  return (
    <div className="admin-container">
      <h2>Панель управления книгами и складами</h2>

      <div className="admin-tabs">
        <button onClick={() => setView('books')} className={view === 'books' ? 'active' : ''}>
          Список книг
        </button>
        <button onClick={() => setView('warehouses')} className={view === 'warehouses' ? 'active' : ''}>
          Список складов
        </button>
      </div>

      {view === 'books' && (
        <div className="admin-book-list">
          <h3>Список книг</h3>
          {currentBooks.map((book) => (
            <div key={book.id} className="admin-book-item">
              <p>
                {book.title} - {book.author} ({book.price} ₽) | На складе: {book.stock}
              </p>
            </div>
          ))}
          <div className="pagination">
            {Array.from({ length: totalPages }, (_, i) => i + 1).map((number) => (
              <button
                key={number}
                onClick={() => paginate(number)}
                className={currentPage === number ? 'active' : ''}
              >
                {number}
              </button>
            ))}
          </div>
        </div>
      )}

      {view === 'warehouses' && (
        <div className="admin-warehouse-list">
          <h3>Список складов</h3>
          {warehouses.map((warehouse) => (
            <div key={warehouse.id} className="admin-warehouse-item">
              <p>
                {warehouse.name} - {warehouse.location} (ID: {warehouse.id})
              </p>
            </div>
          ))}
        </div>
      )}

      <form onSubmit={handleAddBook} className="admin-form">
        <h3>Добавить книгу</h3>
        <div>
          <input
            type="text"
            name="title"
            placeholder="Название"
            value={newBook.title}
            onChange={handleBookInputChange}
            required
          />
          {formErrors.title && <p className="admin-error">{formErrors.title}</p>}
        </div>
        <div>
          <input
            type="text"
            name="author"
            placeholder="Автор"
            value={newBook.author}
            onChange={handleBookInputChange}
            required
          />
          {formErrors.author && <p className="admin-error">{formErrors.author}</p>}
        </div>
        <div>
          <input
            type="number"
            step="0.01"
            name="price"
            placeholder="Цена"
            value={newBook.price}
            onChange={handleBookInputChange}
            required
          />
          {formErrors.price && <p className="admin-error">{formErrors.price}</p>}
        </div>
        <div>
          <input
            type="text"
            name="description"
            placeholder="Описание"
            value={newBook.description}
            onChange={handleBookInputChange}
          />
        </div>
        <div>
          <input
            type="text"
            name="publisher"
            placeholder="Издатель"
            value={newBook.publisher}
            onChange={handleBookInputChange}
          />
        </div>
        <div>
          <input
            type="number"
            name="publicationYear"
            placeholder="Год издания"
            value={newBook.publicationYear}
            onChange={handleBookInputChange}
            required
          />
          {formErrors.publicationYear && <p className="admin-error">{formErrors.publicationYear}</p>}
        </div>
        <div>
          <input
            type="text"
            name="isbn"
            placeholder="ISBN (13 символов)"
            value={newBook.isbn}
            onChange={handleBookInputChange}
          />
          {formErrors.isbn && <p className="admin-error">{formErrors.isbn}</p>}
        </div>
        <div>
          <input
            type="number"
            name="stock"
            placeholder="Общее количество на складе"
            value={newBook.stock}
            onChange={handleBookInputChange}
            required
          />
          {formErrors.stock && <p className="admin-error">{formErrors.stock}</p>}
        </div>
        <div>
          <select
            name="categoryId"
            value={newBook.categoryId}
            onChange={handleBookInputChange}
            required
          >
            <option value="">Выберите категорию</option>
            {categories.map((category) => (
              <option key={category.id} value={category.id}>{category.name}</option>
            ))}
          </select>
          {formErrors.categoryId && <p className="admin-error">{formErrors.categoryId}</p>}
        </div>
        <button type="submit">Добавить книгу</button>
      </form>

      <form onSubmit={handleAddWarehouse} className="admin-form">
        <h3>Добавить склад</h3>
        <div>
          <input
            type="text"
            name="name"
            placeholder="Название склада"
            value={newWarehouse.name}
            onChange={handleWarehouseInputChange}
            required
          />
          {formErrors.name && <p className="admin-error">{formErrors.name}</p>}
        </div>
        <div>
          <input
            type="text"
            name="location"
            placeholder="Местоположение"
            value={newWarehouse.location}
            onChange={handleWarehouseInputChange}
          />
        </div>
        <button type="submit">Добавить склад</button>
      </form>

      <form onSubmit={handleDistributeBook} className="admin-form">
        <h3>Распределение книг по складам</h3>
        <div>
          <select name="bookId" value={distribution.bookId} onChange={handleDistributionChange} required>
            <option value="">Выберите книгу</option>
            {books.map((book) => (
              <option key={book.id} value={book.id}>{book.title} - {book.author}</option>
            ))}
          </select>
          {formErrors.bookId && <p className="admin-error">{formErrors.bookId}</p>}
        </div>
        <div>
          <select name="warehouseId" value={distribution.warehouseId} onChange={handleDistributionChange} required>
            <option value="">Выберите склад</option>
            {warehouses.map((warehouse) => (
              <option key={warehouse.id} value={warehouse.id}>{warehouse.name} ({warehouse.location})</option>
            ))}
          </select>
          {formErrors.warehouseId && <p className="admin-error">{formErrors.warehouseId}</p>}
        </div>
        <div>
          <input
            type="number"
            name="stock"
            placeholder="Количество"
            value={distribution.stock}
            onChange={handleDistributionChange}
            required
          />
          {formErrors.stock && <p className="admin-error">{formErrors.stock}</p>}
        </div>
        <button type="submit">Распределить книгу</button>
      </form>
    </div>
  );
};

export default AdminPanel;
