import React, { useState } from 'react';
import { useAuth } from '../hooks/useAuth';
import { topUpBalance, updateProfile } from '../services/api';
import { toast } from 'react-toastify';
import './ProfilePage.css';

const ProfilePage = () => {
  const { user, setUser } = useAuth();
  const [amount, setAmount] = useState('');
  const [isEditing, setIsEditing] = useState(false);
  const [editData, setEditData] = useState({ login: '', email: '' });

  React.useEffect(() => {
    if (user) {
      setEditData({ login: user.login || '', email: user.email || '' });
    }
  }, [user]);

  const handleTopUp = async () => {
    if (!amount || parseFloat(amount) <= 0) {
      toast.error('Сумма должна быть больше 0');
      return;
    }

    try {
      const response = await topUpBalance(parseFloat(amount));
      setUser(response);
      toast.success('Баланс пополнен!');
      setAmount('');
    } catch (err) {
      toast.error('Ошибка пополнения баланса');
      console.error(err);
    }
  };

  const handleEditToggle = () => {
    setIsEditing(!isEditing);
  };

  const handleEditChange = (e) => {
    setEditData({ ...editData, [e.target.name]: e.target.value });
  };

  const handleSaveProfile = async () => {
    console.log('Sending profile data:', editData); // Логируем отправляемые данные
    try {
      const response = await updateProfile(editData);
      setUser(response);
      setIsEditing(false);
      toast.success('Профиль обновлён!');
    } catch (err) {
      toast.error('Ошибка обновления профиля');
      console.error(err);
    }
  };

  if (!user) return <p>Пожалуйста, войдите в аккаунт</p>;

  return (
    <div className="profile-container">
      <h2>Профиль</h2>
      {isEditing ? (
        <div className="edit-profile-section">
          <h3>Редактирование профиля</h3>
          <input
            type="text"
            name="login"
            value={editData.login}
            onChange={handleEditChange}
            placeholder="Логин"
          />
          <input
            type="email"
            name="email"
            value={editData.email}
            onChange={handleEditChange}
            placeholder="Email"
          />
          <button onClick={handleSaveProfile}>Сохранить</button>
          <button onClick={handleEditToggle}>Отмена</button>
        </div>
      ) : (
        <>
          <p>Логин: {user.login}</p>
          <p>Email: {user.email}</p>
          <p>Баланс: {user.balance || 0} ₽</p>
          <button onClick={handleEditToggle}>Редактировать профиль</button>
        </>
      )}
      <div className="topup-section">
        <h3>Пополнить баланс</h3>
        <input
          type="number"
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
          placeholder="Введите сумму"
        />
        <button onClick={handleTopUp}>Пополнить</button>
      </div>
    </div>
  );
};

export default ProfilePage;
