import axios from 'axios';

const api = axios.create({
  baseURL: 'http://localhost:8080/api',
  headers: {
    'Content-Type': 'application/json',
  },
});

api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('token');
    // Не добавляем токен для регистрации и логина
    if (token && !config.url.includes('/auth/register') && !config.url.includes('/auth/login')) {
      config.headers.Authorization = `Bearer ${token}`;
      console.log('Sending request:', config.method, config.url, 'with token:', token);
    } else {
      console.warn('No token or skipping token for request:', config.method, config.url);
    }
    return config;
  },
  (error) => {
    console.error('Request error:', error);
    return Promise.reject(error);
  }
);
api.interceptors.response.use(
  (response) => {
    console.log('Response:', response.status, response.config.method, response.config.url, response.data);
    return response.data;
  },
  (error) => {
    if (error.response) {
      console.error('Error Response:', error.response.status, error.response.config.method, error.response.config.url, error.response.data);
      if (error.response.status === 403) {
        console.error('403 Forbidden:', error.response.data);
        console.error('Token:', localStorage.getItem('token'));
      }
    } else if (error.request) {
      console.error('No response received:', error.request);
    } else {
      console.error('Error:', error.message);
    }
    return Promise.reject(error);
  }
);

export const loginUser = (loginData) => api.post('/auth/login', loginData);
export const registerUser = (registerData) => api.post('/auth/register', registerData);
export const getBooks = () => api.get('/books');
export const getBookById = (id) => api.get(`/books/${id}`);
export const searchBooks = (title) => api.get(`/books/search?title=${title}`); // Убедитесь, что этот маршрут существует
export const getAddresses = () => api.get('/addresses');
export const addAddress = (addressData) => api.post('/addresses', addressData);
export const createOrder = (orderData) => api.post('/orders', orderData);
export const getOrders = () => api.get('/orders');
export const createBook = (bookData) => api.post('/books', bookData);
export const updateBook = (id, bookData) => api.put(`/books/${id}`, bookData);
export const deleteBook = (id) => api.delete(`/books/${id}`);

export default api;
